<template>
  <div class="profile-card">
    <p class="text-center">
        <img :src="activeUser.avatar" alt="" class="avatar-xlarge img-update">
    </p>

    <div class="form-group">
        <input type="text" v-model="activeUser.username" placeholder="Username" class="form-input text-lead text-bold" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%; cursor: auto;" autocomplete="off">
    </div>

    <div class="form-group">
        <input type="text" v-model="activeUser.name" placeholder="Full Name" class="form-input text-lead">
    </div>

    <div class="form-group">
        <label for="user_bio">Bio</label>
        <textarea class="form-input" id="user_bio" placeholder="Write a few words about yourself." v-model="activeUser.bio"></textarea>
    </div>

    <div class="stats">
      <span>{{userPostsCount}} posts</span>
      <span>{{userThreadsCount}} threads</span>
    </div>
    <hr>
    <div class="form-group">
      <label class="form-label" for="user_website">Website</label>
      <input autocomplete="off" class="form-input" id="user_website" v-model="activeUser.website" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;">
    </div>
    <div class="form-group">
      <label class="form-label" for="user_email">Email</label>
      <input autocomplete="off" class="form-input" id="user_email" v-model="activeUser.email" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;">
    </div>
    <div class="form-group">
      <label class="form-label" for="user_location">Location</label>
      <input autocomplete="off" class="form-input" id="user_location" v-model="activeUser.location" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%;">
    </div>
    <div class="btn-group space-between">
      <button @click.prevent="cancel()" class="btn-ghost">Cancel</button>
      <button @click.prevent="save()" class="btn-blue">Save</button>
    </div>
  </div>
</template>
<script>
export default {
  props: {
    user: {
      required: true,
      type: Object
    },

    userPostsCount: {
      required: true,
      type: Number
    },

    userThreadsCount: {
      required: true,
      type: Number
    }
  },

  data () {
    return {
      activeUser: {...this.user}
    }
  },

  methods: {
    save () {
      this.$store.dispatch('updateUser', {...this.activeUser})
      this.$router.push({name: 'Profile'})
    },

    cancel () {
      this.$router.push({name: 'Profile'})
    }
  }
}
</script>

